import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import axios from 'axios';

function SignIn() {
    const [navigateTo, setNavigateTo] = useState(false)
    const [passengerState, setPassengerState] = useState(
        {
            userName: '',
            password: ''
        }
    );
    const navigate = useNavigate();

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.get(`http://localhost:3001/passenger/signIn`, passengerState)
            .then(response => {
                if (response.status === 200) {
                    setNavigateTo(true)

                }
            })
            .catch(err => console.warn(err));
        alert("check passed");
    };
    if (navigateTo) {
        navigate('/MyComp');
    }
    return (
        <form class="row g-3">
            <div class="container overflow-hidden">
                <div class="col-md-6">
                    <div class="row g-3">
                        {/* <div class="col">
                            <input type="text" class="form-control" placeholder="שם משתמש" aria-label="שם משתמש" value={passengerState.userName}
                                onChange={(e) => setPassengerState({ ...passengerState, userName: e.target.value })} />
                        </div> */}
                        <div class="col">
                            <input type="text" class="form-control" placeholder="סיסמא" aria-label="סיסמא" value={passengerState.password}
                                onChange={(e) => setPassengerState({ ...passengerState, password: e.target.value })} />
                        </div>
                    </div>

                    <div class="form-check">
                        <input class="form-check-input" type="checkbox" id="gridCheck" />
                        <label class="form-check-label" for="gridCheck">
                            זכור אותי
                        </label>
                    </div>
                    <button type="submit" class="btn btn-primary">הכנס</button>
                </div>
            </div>
        </form>
    )
}
export default SignIn;
